package gr.training.basic;

public class Loops1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//me ayth th klassi tha doume mia symantiki leitourgikotita ta lopps(vroxoi)
		
			 //to int i=0 orizei mia akeraia metavliti i kai thn arxikopoiei se 0.
			//me to ervtimatiko kanoume diaxorismo stoixiwn sthn for entoli.
			//to epomeno stoixeio tis for entolis einai h ekfrasi,oso isxuei h ekfrash oso einai true epanalamvanete h entoli.
			//sto 3 kai teleutaio tmhma ths for entolis einai to vima.me thn entoli i++ auxanoume to i kata ena.
			//arxise apo to 0 afxane kata 1 se kathe epanaliphi kai otan ftaseis sto 10 termatise.
		for (int i= 0 ; i<10; i++){
			System.out.println(i);
			
		}
		System.out.println("deutero");
		for (int i= 1 ; i<10; i++){
			System.out.println(i);
			
		}
		System.out.println("trito");
		for (int i= 1 ; i<=10; i++){
			System.out.println(i);
			
	}
	
		System.out.println("tetarto");
		for (int i= 1 ; i<=10; i=i+2){
			System.out.println(i);
			
	}
		System.out.println("pempto");
		for (int i= 1 ; i<=10; i=i+3){
			System.out.println(i);
			
	}
		System.out.println("ekto");
		//for (int i= 1 ; i<=10; i=i-3){
			//System.out.println(i);
			
	//}
		
}
}

